   Console.WriteLine(\"Hello Word!\");\r\n                if (i > 6)\r\n                {\r\n                    break;  //向下跳转，跳出整个循环，之后的循环不再执行\r\n                    //continue; //向上跳，跳出当前这一次的循环，继续下一次的循环\r\n                }\r\n                Console.WriteLine(\"Hi Word!\");\r\n            }\r\n            Console.ReadLine();\r\n        }\r\n    }\r\n}\r\n</pre>\r\n<pre class=\"prettyprint linenums\" >\r\nusing System;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\n\r\nnamespace Array\r\n{\r\n    class Program\r\n    {\r\n        static void Main(string[] args)\r\n        {\r\n            /*\r\n            //1、声明\r\n            int[] a;\r\n            //2、分配内存空间\r\n            a = new int[5];\r\n            //3、赋值\r\n            a[0] = 34;\r\n            a[1] = 67;\r\n            a[2] = 26;\r\n            a[3] = 65;\r\n            a[4] = 16;\r\n            //4、访问\r\n            Console.WriteLine(a[2]);\r\n            \r\n            //1、声明 2、分配内存空间\r\n            char[] b = new char[4];\r\n\r\n            //1、声明 2、分配内存空间 3、赋初值\r\n            string[] c = new string[3] { \"Hello\", \"Word\", \"abc\" };\r\n            string[] d = { \"Hello\", \"Word\", \"abc\" };\r\n\r\n            //数组的遍历：将数组中的每一个元素访问一遍\r\n\r\n            //Console.WriteLine(a[0]);\r\n            //......\r\n            //Console.WriteLine(a[4]);\r\n            for (int i = 0; i < a.Length; i++)\r\n            {\r\n                Console.WriteLine(a[i]);//数组a的长度用a.Length表示\r\n            }\r\n            //上面的for语句可以用于遍历一维数组，但是如果是比一维数组更复杂的结构，for语句会变得复杂（需要嵌套）          \r\n            //用于遍历的语句foreach，无视需要遍历的数据结构\r\n            foreach (int n in a)\r\n            {\r\n                Console.WriteLine(n);\r\n            }\r\n            //等价于\r\n            for (int i = 0; i < a.Length; i++)\r\n            {\r\n                int n = a[i];\r\n                Console.WriteLine(n);\r\n            }\r\n            */\r\n            //二维数组 \r\n            //1、声明 2