MB,\r\n	MAXSIZE=25MB,\r\n	FILEGROWTH=2MB\r\n)\r\n--5、修改Bedroomdatabase数据库1\r\nALTER DATABASE Bedroomdatabase\r\nADD FILE(\r\n	NAME=''Bedroom3_dat'',\r\n	FILENAME=''D:\\Bedroom3_dat.ndf'',\r\n	SIZE=5MB,\r\n	MAXSIZE=10MB,\r\n	FILEGROWTH=1MB\r\n)\r\nGO\r\nALTER DATABASE Bedroomdatabase\r\nADD  LOG FILE(\r\n	NAME=''Bedroom3_log'',\r\n	FILENAME=''D:\\Bedroom3_log.ldf'',\r\n	SIZE=5MB,\r\n	MAXSIZE=10MB,\r\n	FILEGROWTH=1MB\r\n)\r\nGO\r\n--6、修改Bedroomdatabase数据库2\r\nALTER DATABASE Bedroomdatabase\r\nADD FILEGROUP BedroomFileGroup\r\nGO\r\nALTER DATABASE Bedroomdatabase\r\nADD FILE(\r\n	NAME=''Bedroom4_dat'',\r\n	FILENAME=''D:\\Bedroom4_dat.ndf'',\r\n	SIZE=3MB,\r\n	MAXSIZE=10MB,\r\n	FILEGROWTH=1MB\r\n),\r\n(\r\n	NAME=''Bedroom5_dat'',\r\n	FILENAME=''D:\\Bedroom5_dat.ndf'',\r\n	SIZE=3MB,\r\n	MAXSIZE=10MB,\r\n	FILEGROWTH=1MB\r\n)\r\nTO FILEGROUP BedroomFileGroup\r\nGO\r\nALTER DATABASE Bedroomdatabase\r\nREMOVE FILE Bedroom3_log\r\n--7、删除Bedroomdatabase数据库\r\nDROP DATABASE Bedroomdatabase\r\n--8、分离学生宿舍管理数据库，然后附加学生宿舍管理数据库\r\nsp_detach_db 学生宿舍管理\r\nCREATE DATABASE 学生宿舍管理\r\nON(FILENAME=''D:\\学生宿舍管理.mdf'')\r\nFOR ATTACH\r\n--9、创建学生宿舍管理数据库快照\r\nCREATE DATABASE 学生宿舍管理_snapshot\r\nON(\r\n	NAME=''学生宿舍管理'',\r\n	FILENAME=''D:\\学生宿舍管理_snapshot.snp''\r\n)\r\nAS snapshot of 学生宿舍管理\r\nGO\r\n--10、收缩学生宿舍管理数据库\r\n--使用AUTO_SHRINK\r\nALTER DATABASE 学生宿舍管理 SET AUTO_SHRINK ON\r\n--使用DBCC SHRINKDATABASE\r\nDBCC SHRINKDATABASE(''学生宿舍管理'',80)\r\n--使用DBCC SHRINKDFILF\r\nDBCC SHRINKFILE(''学生宿舍管理'',80)\r\n</pre>','SQL Server 语句（一）','','publish','open','open','','sql-server-%e8%af%ad%e5%8f%a5%ef%bc%88%e4%b8%80%ef%bc%89','','','2015-07-02 23:09:07','2015-07-02 15:09:07','',0,'http://meowv.com/?p=327',0,'post','',0),(328,1,'2015-06-02 22:20:07','2015-06-02 14:20:07','<pre class=\"prettyprint linenums\" >\r\n--创建学生成绩管理系统\r\nCREATE DATABASE 学生成绩管理系统\r\n--创建学生表\r\nUSE 学生成绩管理系统\r\nGO\r\nCREATE TABLE 学生表\r\n(\r\n	学号 CHAR(9) NOT NULL CONSTRAINT PK_XH PRIMARY KEY,\r\n	姓名 CHAR(8) NOT NULL,\r\n	性别 BIT NULL CONSTRAINT DE_1 DEFAULT 0,\r\n	专业 varchar(50) NULL,\r\n	出生年月 SMALLDATETIME NULL,\r\n	家庭住址 VARCHAR(100) NULL,\r\n	联系电话 CHAR(12) NULL,\r\n	总学分 FLOAT CONSTRAINT DE_2 DEFAULT 0\r\n)\r\n--创建课程表\r\nUSE 学生成绩管理系统\r\nGO\r\nCREATE TABLE 课程表\r\n(\r\n	课程号 CHAR(9) NOT NULL,\r\n	课名 VARCHAR(50) NOT NULL,\r\n	学时 INT NOT NULL CONSTRAINT CH_1 CHECK(学时>=0),\r\n	学分 INT NOT NULL CHECK(学分>=0),\r\n	备注 TEXT,\r\n	CONSTRAINT PK_KH PRIMARY KEY(课程号)\r\n)\r\n--创建选课表\r\nUSE 学生成绩管理系统\r\nGO\r\nCREATE TABLE 选课表\r\n(\r\n	学号 CHAR(9) NOT NULL,\r\n	课程号 CHAR(9) NOT NULL CONSTRAINT FK_1 FOREIGN KEY REFERENCES 课程表(课程号), \r\n	成绩 FLOAT NULL,\r\n	CONSTRAINT CH_CJ CHECK(成绩>=0 AND 成绩<=100),\r\n	CONSTRAINT PK_XH1 PRIMARY KEY(学号,课程号),\r\n	CONSTRAINT FK_2 FOREIGN KEY(学号)REFERENCES 学生表(学号)\r\n)\r\n--创建教师表\r\nUSE 学生成绩管理系统\r\nGO\r\nCREATE TABLE 教师表\r\n(\r\n	教师号 CHAR(6) NOT NULL,\r\n	姓名 CHAR(8) NOT NULL,\r\n	职称 CHAR(8) NOT NULL,\r\n	部门编号 VARCHAR(50) NOT NULL,\r\n	联系方式 CHAR(12) NULL,\r\n	PRIMARY KEY(教师号)\r\n	\r\n)\r\n--创建部门表\r\nUSE 学生成绩管理系统\r\nGO\r\nCREATE TABLE 部门表\r\n(\r\n	部门编号 VARCHAR(50) NOT NULL PRIMARY KEY,\r\n	部门名称 VARCHAR(50) NOT NULL,\r\n	部门地址 VARCHAR(100) NULL\r\n)\r\n--创建授课表\r\nUSE 学生成绩管理系统\r\nGO\r\nCREATE TABLE 授课表\r\n(\r\n	教师号 CHAR(6) NOT NULL FOREIGN KEY REFERENCES 教师表(教师号),\r\n	课程号 CHAR(9) NOT NULL FOREIGN KEY REFERENCES 课程表(课程号),\r\n	开课时间 SMALLDATETIME NULL,\r\n	PRIMARY KEY(教师号,课程号)\r\n)\r\n--规则和约束\r\n\r\n	--规则\r\nCREATE RULE RULE_XB AS @XB=0 OR @XB=1	--建立规则名为RULE_XB\r\nGO\r\n\r\nSP_BINDRULE ''RULE_XB'',''学生表.性别''	--将RULE_XB规则绑定到学生表中的性别上\r\n\r\nSP_UNBINDRULE ''学生表.性别''	--将RULE_XB规则绑定解除\r\n\r\nDROP RULE RULE_XB	--删除规则\r\n\r\n	--默认\r\nCREATE DEFAULT DEFAULT_XF AS 3	--建立默认\r\nGO\r\n\r\nSP_BINDEFAULT ''DEFAULT_XF'', ''课程表.学分''	--将DEFAULT_XF默认绑定到课程表中的学分上\r\n\r\nSP_UNBINDEFAULT ''课程表.学分''	--将DEFAULT_XF默认绑定解除\r\n\r\nDROP DEFAULT DEFAULT_XF		--删除默认\r\n\r\n--查看学生表属性\r\nUSE 学生成绩管理系统\r\nGO\r\nSP_HELP 学生表\r\n</pre>\r\n<pre class=\"prettyprint linenums\" >\r\n--将授课表中开课时间的数据类型修改为DATATIME\r\nALTER TABLE 授课表\r\nALTER COLUMN 开课时间 DATATIME\r\n\r\n--将授课表中开课时间的数据类型修改为SMALLDATETIME\r\nALTER TABLE 授课表\r\nALTER COLUMN 开课时间 SMALLDATETIME\r\n\r\n--在授课表添加一个字段\r\nALTER TABLE 授课表\r\nADD 开课时间 VARCHAR(20) NULL\r\n\r\n--在课程表中添加一个字段\r\nALTER TABLE 授课表\r\nADD 序号 SMALLINT NOT NULL IDENTITY(1,1) CONSTRAINT UN_1 UNIQUE\r\n\r\n--将课程表中添加的字段 开课地点 删除\r\nALTER TABLE 授课表\r\nDROP COLUMN 开课时间\r\n\r\nALTER TABLE 课程表\r\nDROP CONSTRAINT UN_1\r\n\r\nALTER TABLE 课程表\r\nDROP COLUMN 序号\r\n\r\n--将教师表中的职称默认为讲师\r\nALTER TABLE 教师表\r\nADD CONSTRAINT DE_2 DEFAULT ''讲师'' FOR 职称\r\n\r\n--将课程表中的学分默认为4\r\nALTER TABLE 课程表\r\nADD DEFAULT 4 FOR 学分\r\n\r\n--将默认约束DE_2删除\r\nALTER TABLE 教师表\r\nDROP CONSTRAINT DE_2\r\n\r\n--将选课表中的复合主键PK_XH1删除\r\nALTER TABLE 选课表\r\nDROP CONSTRAINT PK_XH1\r\n\r\n--添加选课表中的学号及课程号的复合主键PK_XH1\r\nALTER TABLE 选课表\r\nADD CONSTRAINT PK_XH1 PRIMARY KEY(学号,课程号)\r\n\r\n--将选课表中的外键FK_1删除\r\nALTER TABLE 选课表\r\nDROP CONSTRAINT FK_1\r\n\r\n--添加上述删除的选课表中的外键FK_1\r\nALTER TABLE 选课表\r\nADD CONSTRAINT FK_1 FOREIGN KEY(学号) REFERENCES 学生表(学号)\r\n\r\n--在学生表中为联系电话建立唯一约束UN_2\r\nALTER TABLE 学生表\r\nADD CONSTRAINT UN_2 UNIQUE(联系电话)\r\n\r\n--将授课表删除\r\nDROP TABLE 授课表\r\n\r\n--将学生表、选课表删除\r\nDROP TABLE 学生表,选课表\r\n\r\n--将教师表更名为教师表1\r\nSP_RENAME ''教师表'',''教师表1''\r\n\r\n--将教师表1更名为教师表\r\nSP_RENAME ''教师表1'',''教师表''\r\n\r\n--将学生表中的学号更名为学号1\r\nUSE 学生成绩管理\r\nGO\r\nSP_RENAME ''学生表.学号'',''学号1''\r\n\r\n--将学生表中的学号1更名为学号\r\nUSE 学生成绩管理\r\nGO\r\nSP_RENAME ''学生表.学号1'',''学号''\r\n\r\n\r\n--在课程表中添加一行记录\r\nUSE 学生成绩管理\r\nGO\r\nINSERT 课程表(课程号,课名,学分,学时,备注)VALUES(''1001'',''数据库技术'',4,72,''适合电子信息专业课程'')\r\n--或者 INSERTINTO 课程表(课程号,课名,学分,学时,备注)VALUES(''1001'',''数据库技术'',4,72,''适合电子信息专业课程'')\r\n\r\n\r\n--在课程表中添加一条记录\r\nUSE 学生成绩管理\r\nGO\r\nINSERT 课程表 VALUES(''1002'',''C语言程序设计'',4,72,''适合电子信息专业课程'')\r\n\r\n--在课程表中添加一条记录\r\nUSE 学生成绩管理\r\nGO\r\nINSERT 课程表(课程号,课名,学分,学时)VALUES(''2001'',''大学语文'',3,60)\r\n\r\n--将课程表中课程号为2001的课名改为应用文写作\r\nUPDATE 课程表 SET 课名=''应用文写作''WHERE 课程号=''2001''\r\n\r\n--在为课程表输入数据时，误将课程号为1002的记录的学时、学分输反了，改正\r\nUSE 学生成绩管理\r\nGO\r\nUPDATE 课程表 SET 学分=4,学时=72