t linenums\" >\r\nusing System.Web;\r\n\r\nnamespace SessionHelper2\r\n{\r\n    public static class SessionHelper2\r\n    {\r\n        /// <summary>\r\n        /// 添加Session，调动有效期为20分钟\r\n        /// </summary>\r\n        /// <param name=\"strSessionName\">Session对象名称</param>\r\n        /// <param name=\"strValue\">Session值</param>\r\n        public static void Add(string strSessionName, string strValue)\r\n        {\r\n            HttpContext.Current.Session[strSessionName] = strValue;\r\n            HttpContext.Current.Session.Timeout = 20;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 添加Session，调动有效期为20分钟\r\n        /// </summary>\r\n        /// <param name=\"strSessionName\">Session对象名称</param>\r\n        /// <param name=\"strValues\">Session值数组</param>\r\n        public static void Adds(string strSessionName, string[] strValues)\r\n        {\r\n            HttpContext.Current.Session[strSessionName] = strValues;\r\n            HttpContext.Current.Session.Timeout = 20;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 添加Session\r\n        /// </summary>\r\n        /// <param name=\"strSessionName\">Session对象名称</param>\r\n        /// <param name=\"strValue\">Session值</param>\r\n        /// <param name=\"iExpires\">调动有效期（分钟）</param>\r\n        public static void Add(string strSessionName, string strValue, int iExpires)\r\n        {\r\n            HttpContext.Current.Session[strSessionName] = strValue;\r\n            HttpContext.Current.Session.Timeout = iExpires;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 添加Session\r\n        /// </summary>\r\n        /// <param name=\"strSessionName\">Session对象名称</param>\r\n        /// <param name=\"strValues\">Session值数组</param>\r\n        /// <param name=\"iExpires\">调动有效期（分钟）</param>\r\n        public static void Adds(string strSessionName, string[] strValues, int iExpires)\r\n        {\r\n            HttpContext.Current.Session[strSessionName] = strValues;\r\n            HttpContext.Current.Session.Timeout = iExpires;\r\n        }\r\n\r\n        /// <summary>\r\n        /// 读取某个Session对象值\r\n        /// </summary>\r\n        /// <param name=\"strSessionName\">Session对象名称</param>\r\n        /// <returns>Session对象值</returns>\r\n        public static string Get(string strSessionName)\r\n        {\r\n            if (HttpContext.Current.Session[strSessionName] == null)\r\n            {\r\n                return null;\r\n            }\r\n            else\r\n            {\r\n                return HttpContext.Current.Session[strSessionName].ToString();\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 读取某个Session对象值数组\r\n        /// </summary>\r\n        /// <param name=\"strSessionName\">Session对象名称</param>\r\n        /// <returns>Session对象值数组</returns>\r\n        public static string[] Gets(string strSessionName)\r\n        {\r\n            if (HttpContext.Current.Session[strSessionName] == null)\r\n            {\r\n                return null;\r\n            }\r\n            else\r\n            {\r\n                return (string[])HttpContext.Current.Session[strSessionName];\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 删除某个Session对象\r\n        /// </summary>\r\n        /// <param name=\"strSessionName\">Session对象名称</param>\r\n        public static void Del(string strSessionName)\r\n        {\r\n            HttpContext.Current.Session[strSessionName] = null;\r\n        }\r\n    }\r\n}\r\n</pre>','SessionHelper','','publish','open','open','','sessionhelper','','','2016-03-06 22:00:57','2016-03-06 14:00:57','',0,'http://meowv.com/?p=585',0,'post','',0),(586,1,'2016-03-08 11:51:22','2016-03-08 03:51:22','<pre class=\"prettyprint linenums\" >\r\nusing System.Data;\r\nusing System.IO;\r\n\r\nnamespace CsvHelper\r\n{\r\n    /// <summary>\r\n    /// CSV文件转换类\r\n    /// </summary>\r\n    public static class CsvHelper\r\n    {\r\n        /// <summary>\r\n        /// 导出报表为Csv\r\n        /// </summary>\r\n        /// <param name=\"dt\">DataTable</param>\r\n        /// <param name=\"strFilePath\">物理路径</param>\r\n        /// <param name=\"tableheader\">表头</param>\r\n        /// <param name=\"columname\">字段标题,逗号分隔</param>\r\n        public static bool dt2csv(DataTable dt, string strFilePath, string tableheader, string columname)\r\n        {\r\n            try\r\n            {\r\n                string strBufferLine = \"\";\r\n                StreamWriter strmWriterObj = new StreamWriter(strFilePath, false, System.Text.Encoding.UTF8);\r\n                strmWriterObj.WriteLine(tableheader);\r\n                strmWriterObj.WriteLine(columname);\r\n                for (int i = 0; i < dt.Rows.Count; i++)\r\n                {\r\n                    strBufferLine = \"\";\r\n                    for (int j = 0; j < dt.Columns.Count; j++)\r\n                    {\r\n                        if (j > 0)\r\n                            strBufferLine += \",\";\r\n                        strBufferLine += dt.Rows[i][j].ToString();\r\n                    }\r\n                    strmWriterObj.WriteLine(strBufferLine);\r\n                }\r\n                strmWriterObj.Close();\r\n                return true;\r\n            }\r\n            catch\r\n            {\r\n                return false;\r\n            }\r\n        }\r\n\r\n        /// <summary>\r\n        /// 将Csv读入DataTable\r\n        /// </summary>\r\n        /// <param name=\"filePath\">csv文件路径</param>\r\n        /// <param name=\"n\">表示第n行是字段title,第n+1行是记录开始</param>\r\n        public static DataTable csv2dt(string filePath, int n, DataTable dt)\r\n        {\r\n            StreamReader reader = new StreamReader(filePath, System.Text.Encoding.UTF8, false);\r\n            int i = 0, m = 0;\r\n            reader.Peek();\r\n            while (reader.Peek() > 0)\r\n            {\r\n                m = m + 1;\r\n                string str = reader.ReadLine();\r\n                if (m >= n + 1)\r\n                {\r\n                    string[] split = str.Split('','');\r\n\r\n                    System.Data.DataRow dr = dt.NewRow();\r\n                    for (i = 0; i < split.Length; i++)\r\n                    {\r\n                        dr[i] = split[i];\r\n                    }\r\n                    dt.Rows.Add(dr);\r\n                }\r\n            }\r\n            return dt;\r\n        }\r\n    }\r\n}\r\n</pre>','CsvHelper','','publish','open','open','','csvhelper','','','2016-03-08 11:51:22','2016-03-08 03:51:22','',0,'http://meowv.com/?p=586',0,'post','',0),(589,1,'2016-03-09 19:33:16','2016-03-09 11:33:16','','1','','inherit','open','closed','','1-10','','','2016-03-09 19:33:16','2016-03-09 11:33:16','',0,'http://meowv.com/wp-content/uploads/2016/03/1.png',0,'attachment','image/png',0),(590,1,'2016-03-12 14:14:44','2016-03-12 06:14:44','<img src=\"http://meowv.com/wp-content/uploads/2016/03/MacBook.jpg\" alt=\"MacBook\" width=\"1126\" height=\"656\" class=\"aligncenter size-full wp-image-591\" />\r\n<link rel=''stylesheet'' type=''text/css'' href=''http://tools.oschina.net/js/syntaxhighlighter_3.0.83/styles/shCoreEmacs.css''/><div id=\"highlighter_783043\" class=\"syntaxhighlighter  xml\"><div class=\"toolbar\"><span><a href=\"#\" class=\"toolbar_item command_help help\">?</a></span></div><table border=\"0\" cellpadding=\"0\" cellspacing=\"0\"><tbody><tr><td class=\"gutter\"><div class=\"line number1 index0 alt2\">1</div><div class=\"line number2 index1 alt1\">2</div><div class=\"line number3 index2 alt2\">3</div><div class=\"line number4 index3 alt1\">4</div><div class=\"line number5 index4 alt2\">5</div><div class=\"line number6 index5 alt1\">6</div><div class=\"line number7 index6 alt2\">7</div><div class=\"line number8 index7 alt1\">8</div><div class=\"line number9 index8 alt2\">9</div><div class=\"line number10 index9 alt1\">10</div><div class=\"line number11 index10 alt2\">11</div><div class=\"line number12 index11 alt1\">12</div><div class=\"line number13 index12 alt2\">13</div>